    def test_creator(self):
        # Outputs
        scriptPubKey_1 = unhexlify('0014d85c2b71d0060b09c9886aeb815e50991dda124d')
        amount_1 = 1.49990000 
        scriptPubKey_2 = unhexlify('001400aea9a2e5f0f876a588df5546e8742d1d87008f')
        amount_2 = 1.00000000
        # Inputs
        utxo_1 = unhexlify('75ddabb27b8845f5247975c8a5ba7c6f336c4570708ebe230caf6db5217ae858')
        index_1 = 0
        utxo_2 = unhexlify('1dea7cd05979072a3578cab271c02244ea8a090bbb46aa680a65ecd027048d83')
        index_2 = 1
        # Target psbt in hex
        want = unhexlify('')
        ins = [(utxo_1, index_1),(utxo_2, index_2)]
        outs = [(int(amount_1*100000000), scriptPubKey_1),(int(amount_2*100000000), scriptPubKey_2)]
        creator_test = Creator(ins, outs)
        test_psbt = creator_test.serialized()
        # creator_test.make_file()
        self.assertEqual(test_psbt, want)
        
        
    def test_parse1(self):
        want = unhexlify('')
        stream = BytesIO(want)
        new_psbt = psbt.parse(stream)
        self.assertEqual(new_psbt.serialize(), want, 'Failure testing parse after Creator test')
        
        
