    def test_updater(self):
        start_psbt = (unhexlify(''))
        updater_test = Updater(start_psbt)
        
        # First Input
        non_wit_utxo = unhexlify('')
        updater_test.add_nonwitness_utxo(input_index=0, utxo=non_wit_utxo)
        # Sighash = SIGHASH_ALL
        updater_test.add_sighash_type(input_index=0, sighash=1)
        # Scripts
        redeem_script_1 = unhexlify('5221029583bf39ae0a609747ad199addd634fa6108559d6c5cd39b4c2183f1ab96e07f2102dab61ff49a14db6a7d02b0cd1fbb78fc4b18312b5b4e54dae4dba2fbfef536d752ae')
        updater_test.add_input_redeem_script(input_index=0, script=redeem_script_1)
        # Public keys
        # Master fingerprint: d90c6a4f
        # Key: 029583bf39ae0a609747ad199addd634fa6108559d6c5cd39b4c2183f1ab96e07f
        # Derivation path: m/0'/0'/0'
        pubkey_1 = unhexlify('029583bf39ae0a609747ad199addd634fa6108559d6c5cd39b4c2183f1ab96e07f')
        fingerprint_1 = unhexlify('d90c6a4f')
        path_1 = unhexlify('000000800000008000000080')
        updater_test.add_input_pubkey(input_index=0, pubkey=pubkey_1, masterkey_fingerprint=fingerprint_1, bip32_path=path_1)
        # Key: 02dab61ff49a14db6a7d02b0cd1fbb78fc4b18312b5b4e54dae4dba2fbfef536d7
        # Derivation path: m/0'/0'/1'
        pubkey_2 = unhexlify('02dab61ff49a14db6a7d02b0cd1fbb78fc4b18312b5b4e54dae4dba2fbfef536d7')
        fingerprint_2 = unhexlify('d90c6a4f')
        path_2 = unhexlify('000000800000008001000080')
        updater_test.add_input_pubkey(input_index=0, pubkey=pubkey_2, masterkey_fingerprint=fingerprint_2, bip32_path=path_2)
        
        # Second Input
        wit_utxo = unhexlify('')
        updater_test.add_witness_utxo(input_index=1, utxo=wit_utxo, utxo_index=1)
        # Sighash = SIGHASH_ALL
        updater_test.add_sighash_type(input_index=1, sighash=1)
        # Scripts
        witness_script = unhexlify('522103089dc10c7ac6db54f91329af617333db388cead0c231f723379d1b99030b02dc21023add904f3d6dcf59ddb906b0dee23529b7ffb9ed50e5e86151926860221f0e7352ae')
        updater_test.add_input_witness_script(input_index=1, script=witness_script)
        redeem_script_2 = unhexlify('00208c2353173743b595dfb4a07b72ba8e42e3797da74e87fe7d9d7497e3b2028903')
        updater_test.add_input_redeem_script(input_index=1, script=redeem_script_2)
        # Public keys
        # Master fingerprint: d90c6a4f
        # Key: 023add904f3d6dcf59ddb906b0dee23529b7ffb9ed50e5e86151926860221f0e73
        # Derivation path: m/0'/0'/3'
        pubkey_3 = unhexlify('023add904f3d6dcf59ddb906b0dee23529b7ffb9ed50e5e86151926860221f0e73')
        fingerprint_3 = unhexlify('d90c6a4f')
        path_3 = unhexlify('000000800000008003000080')
        updater_test.add_input_pubkey(input_index=1, pubkey=pubkey_3, masterkey_fingerprint=fingerprint_3, bip32_path=path_3)
        # Master fingerprint: d90c6a4f
        # Key: 03089dc10c7ac6db54f91329af617333db388cead0c231f723379d1b99030b02dc
        # Derivation path: m/0'/0'/2'
        pubkey_4 = unhexlify('03089dc10c7ac6db54f91329af617333db388cead0c231f723379d1b99030b02dc')
        fingerprint_4 = unhexlify('d90c6a4f')
        path_4 = unhexlify('000000800000008002000080')
        updater_test.add_input_pubkey(input_index=1, pubkey=pubkey_4, masterkey_fingerprint=fingerprint_4, bip32_path=path_4)
        # Outputs
        # Public keys
        # Master fingerprint: d90c6a4f
        # Key: 03a9a4c37f5996d3aa25dbac6b570af0650394492942460b354753ed9eeca58771
        # Derivation path: m/0'/0'/4'
        pubkey_5 = unhexlify('03a9a4c37f5996d3aa25dbac6b570af0650394492942460b354753ed9eeca58771')
        fingerprint_5 = unhexlify('d90c6a4f')
        path_5 = unhexlify('000000800000008004000080')
        updater_test.add_output_pubkey(output_index=0, pubkey=pubkey_5, masterkey_fingerprint=fingerprint_5, bip32_path=path_5)
        # Master fingerprint: d90c6a4f
        # Key:027f6399757d2eff55a136ad02c684b1838b6556e5f1b6b34282a94b6b50051096
        # Derivation path: m/0'/0'/5'
        pubkey_6 = unhexlify('027f6399757d2eff55a136ad02c684b1838b6556e5f1b6b34282a94b6b50051096')
        fingerprint_6 = unhexlify('d90c6a4f')
        path_6 = unhexlify('000000800000008005000080')
        updater_test.add_output_pubkey(output_index=1, pubkey=pubkey_6, masterkey_fingerprint=fingerprint_6, bip32_path=path_6) 
    
        result = updater_test.serialized()
        want = unhexlify('')
        # updater_test.make_file()
        self.assertEqual(result, want)
    
    
