    def test_signer1(self):
        # Starting PSBT
        start_psbt = unhexlify('')
        test_signer = Signer(start_psbt)
        # First signature
        sec_1 = unhexlify('02dab61ff49a14db6a7d02b0cd1fbb78fc4b18312b5b4e54dae4dba2fbfef536d7')
        sig_1 = unhexlify('3045022100f61038b308dc1da865a34852746f015772934208c6d24454393cd99bdf2217770220056e675a675a6d0a02b85b14e5e29074d8a25a9b5760bea2816f661910a006ea01')
        test_signer.add_partial_signature(new_sig=sig_1, compressed_sec=sec_1, input_index=None)
        # Second signature
        sec_2 = unhexlify('023add904f3d6dcf59ddb906b0dee23529b7ffb9ed50e5e86151926860221f0e73')
        sig_2 = unhexlify('3044022065f45ba5998b59a27ffe1a7bed016af1f1f90d54b3aa8f7450aa5f56a25103bd02207f724703ad1edb96680b284b56d4ffcb88f7fb759eabbe08aa30f29b851383d201')
        test_signer.add_partial_signature(new_sig=sig_2, compressed_sec=sec_2, input_index=None)
        # test_signer.make_file()
        want = unhexlify('')
        result = test_signer.serialized()
        self.assertEqual(result, want)
    
    
