    def test_valid_psbts(self):
        # Verify that all these PSBTs are valid
        try:
            valid1 = unhexlify('')
            valid1_test = psbt.parse(BytesIO(valid1))
        except:
            self.fail('Fail on case: PSBT with one P2PKH input. Outputs are empty')
        try:
            valid2 = unhexlify('')
            valid2_test = psbt.parse(BytesIO(valid2))
        except:
            self.fail('Fail on case: PSBT with one P2PKH input and one P2SH-P2WPKH input. First input is signed and finalized. Outputs are empty')
        try:
            valid3 = unhexlify('')
            valid3_test = psbt.parse(BytesIO(valid3))
        except:
            self.fail('Fail on case: PSBT with one P2PKH input which has a non-final scriptSig and has a sighash type specified. Outputs are empty')
        try:
            valid4 = unhexlify('')
            valid4_test = psbt.parse(BytesIO(valid4))
        except:
            self.fail('Fail on case: PSBT with one P2PKH input and one P2SH-P2WPKH input both with non-final scriptSigs. P2SH-P2WPKH inputs redeemScript is available. Outputs filled.')
        try:
            valid5 = unhexlify('')
            valid5_test = psbt.parse(BytesIO(valid5))
        except:
            self.fail('Fail on case: PSBT with one P2SH-P2WSH input of a 2-of-2 multisig, redeemScript, witnessScript, and keypaths are available. Contains one signature.')
        try:
            valid6 = unhexlify('70736274ff01003f0200000001ffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff0000000000ffffffff010000000000000000036a010000000000000a0f0102030405060708090f0102030405060708090a0b0c0d0e0f0000')
            valid6_test = psbt.parse(BytesIO(valid6))
        except:
            self.fail('Fail on case: PSBT with unknown types in the inputs.')
            
